import React from 'react';
import 'bootswatch/dist/vapor/bootstrap.css';
import './App.css';



export default class TelaHome extends React.Component {

    state = {
        nomeBordado: '',
        result: ''
    }

    show = () => {

        var result = this.state.nomeBordado;
        console.log(this.state.nomeBordado)
        this.setState({ result });
    }

    render() {
        return (
            <div>
                <label>nomeBordado:</label>
                <input type="text" value={this.state.nomeBordado} onChange={(e) => { this.setState({ nomeBordado: e.target.value }) }} />

                <div className="form-group">
                    <label className="col-form-label mt-4" htmlFor="nomeCor">Nome do Bordado:</label>
                    <input type="text" className="form-control" placeholder="nomeCor" id="nomeCor"
                        value={this.state.nomeBordado} onChange={(e) => { this.setState({ nomeBordado: e.target.value }) }} />
                </div>

                <br />


                <button type="button" className="btn btn-success" onClick={this.show}>Show</button>

                <br />
                <div className="form-group">
                    <fieldset disabled="">
                        <input className="form-control" id="disabledInput" type="text"
                            placeholder="Disabled input here..." disabled="" defaultValue={this.state.result} />
                    </fieldset>
                </div>


                <div className="card text-white bg-primary mb-3" style={this.style.width}>
                    <div className="card-header">Header</div>
                    <div className="card-body">
                        <h4 className="card-title">Primary card title</h4>
                        <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                    </div>

                </div>
            </div>
        )
    }

    style = {
        width: {
            position: 'relative',
            left: '300px'
        }
    }

}
