import React from 'react';
import 'bootswatch/dist/vapor/bootstrap.css';
import './App.css';



export default class App extends React.Component{

    state = {
      nomeCor: '',
      codigoCor: ''
    }

    show = () =>{
      /*
      console.log(this.state.nomeCor);
      console.log(this.state.codigoCor);
      */
      this.setState(this.start.nomeCor);

    }

  render(){
    return(
      <div>

      <div class="form-group">
        <label className="col-form-label mt-4" for="nomeCor">Nome da Cor:</label>
        <input type="text" class="form-control" placeholder="nomeCor" id="nomeCor" value={this.state.nomeCor}
          onChange={(e) => {this.setState({nomeCor: e.target.value})}}/>
      </div>

      <br/>
      <div class="form-group">
        <label className="col-form-label mt-4" for="codigoCor">Codigo da Cor:</label>
        <input type="text" class="form-control" placeholder="codigoCor" id="codigoCor" value={this.state.codigoCor}
          onChange={(e) => {this.setState({codigoCor: e.target.value})}}/>
      </div>

      <br/>

      <button type="button" className="btn btn-success" onClick={this.show}>Show</button>

      <br/>

        <div class="form-group">
          <fieldset disabled="">
            <input className="form-control" id="disabledInput" type="text" placeholder="Disabled input here..." disabled=""
              value={this.state.show}/>
          </fieldset>
        </div>
      </div>
    )
  }
}
